##### Functions
def frequenceLettresTexte(cipher):
    # Pre-processing to split the text
    splittedCipher = list(cipher);
    dicoMotifOccurences = Counter(splittedCipher)
    return dicoMotifOccurences

def normalizeDico(dico):
    #We get the max value of the array (copy/paste internet)
    maxValue = dico[max(dico.items(), key=operator.itemgetter(1))[0]]

    for key, value in dico.items():
        dico[key] = int(value)/int(maxValue)

    return dico

def normalizedFrequencesLettresTexte(cipher):
    dicoMotifOccurences = frequenceLettresTexte(cipher)
    #We get the max value of the array (copy/paste internet)
    dicoMotifOccurences = normalizeDico(dicoMotifOccurences)

    return dicoMotifOccurences

def calculCoincidenceIndice(cipher):
    dicoMotifOccurences = frequenceLettresTexte(cipher)

    # Calculus of occurences for each letter
    accu = 0
    for key, value in dicoMotifOccurences.items():
        accu += value * (value - 1)

    return accu / (len(cipher) * (len(cipher) - 1))


def getTabOfCoincidenceIndice(cipher, minKeyLength, maxKeyLength):
    listCoincIndices = []

    # For all key potential length
    for i in range(minKeyLength, maxKeyLength):
        # Pour une longueur de clé donnée, on considère les <lengthK> sous textes générés, potentiellement monoalphabétiques
        tabIndice = []

        for k in range(0, i):
            tmpCipher = cipher[
                        k::i]  # On commence à la kième lettre et on saute de i lettre en i lettre = On considère le texte chiffré monoalphabétiquement par la kème lettre de la clé courante de longueur i
            # On calcul l'indice de coincidence de chaque sous texte
            tabIndice.append(calculCoincidenceIndice(tmpCipher))

        # On fait la moyenne des indices de coincidence des sous texte et on le stocke comme l'indice de coincidence pour le texte global
        listCoincIndices.append(sum(tabIndice) / i)

    return listCoincIndices

def getKSubtexte(cipher,expectedKeyLength):
    kSubtexte = []

    for k in range(0, expectedKeyLength):
        tmpCipher = cipher[k::expectedKeyLength]  # On commence à la kième lettre et on saute de expectedKeyLength lettre en expectedKeyLength lettre
        kSubtexte.append(tmpCipher)

    return kSubtexte

def getBestKeylength(listCoincIndices):
    distance = []
    for i in range(0, len(listCoincIndices)):
        # Calcul of the distance between current coincidence indice and target coincidence indice
        distance.append(abs(listCoincIndices[i] - coincidenceTragetIndice))

    # Select the best one = the nearest one
    bestIndice = distance.index(min(distance))+1
    return bestIndice

def dictionnaryToArray(dic):
    array = []

    for i in range(0, 26):  # = for each of the 26 letters of the reference alphabet
        array.append(dic[alphabet[i]])

    return array

def computeShift(freqTab,freqOfLetters):
    #Calcul de la correlation entre un texte <LangueChoisie> et le cipher décalé de i position
    correlationParDecalage = []
    arrayDecalable = dictionnaryToArray(freqTab)
    arrayAlphabet = dictionnaryToArray(freqOfLetters)
    ##Verified

    for decalage in range(0,26):
        correlationParLettre = []

        for i in range(0,26) : # = for each of the 26 letters of the reference alphabet
            probaLettreDecalable = arrayDecalable[(i+decalage)%26]
            probaLettreAlphabet = arrayAlphabet[i]

            correlationParLettre.append(probaLettreDecalable * probaLettreAlphabet)
            #print("correlation pour décalage de " + str(i) + " soit : " +  alphabet[i] + " is " + str(correlation[i]))
        print(correlationParLettre)

        correlationParDecalage.append(sum(correlationParLettre))

    print(correlationParDecalage)
    # We select the maximum value = the heighest correlation
    indiceBestCorrelation = correlationParDecalage.index(max(correlationParDecalage))

    return indiceBestCorrelation

def computeTextualKeyFromInt(intTab):
    key = ""
    for i in range(0, len(intTab)):
        key += alphabet[intTab[i]]

    return key

#VERIFIED
def decrypt(cipher, textualKey):
    plaintext = ""

    for index, character in enumerate(cipher):
        currentDecalage = textualKey[index%len(textualKey)]
        character = ord(character)-ord(currentDecalage)
        character = chr(character % 26 + ord('a'))
        plaintext += character
    return plaintext

